pragma solidity ^0.8.0;
pragma experimental ABIEncoderV2;

import "./HarmonyParser.sol";
import "./lib/SafeCast.sol";
import "@openzeppelin/contracts-upgradeable/utils/math/SafeMathUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
// import "openzeppelin-solidity/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
// import "openzeppelin-solidity/contracts/proxy/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

contract HarmonyLightClient is
    Initializable,
    PausableUpgradeable,
    AccessControlUpgradeable
{
    using SafeCast for *;
    using SafeMathUpgradeable for uint256;

    struct BlockHeader {
        bytes32 parentHash;
        bytes32 stateRoot;
        bytes32 transactionsRoot;
        bytes32 receiptsRoot;
        uint256 number;
        uint256 epoch;
        uint256 shard;
        uint256 time;
        bytes32 mmrRoot;
        bytes32 hash;
    }

    event CheckPoint(
        bytes32 stateRoot,
        bytes32 transactionsRoot,
        bytes32 receiptsRoot,
        uint256 number,
        uint256 epoch,
        uint256 shard,
        uint256 time,
        bytes32 mmrRoot,
        bytes32 hash
    );

    BlockHeader firstBlock;
    BlockHeader lastCheckPointBlock;

    // epoch to block numbers, as there could be >=1 mmr entries per epoch
    mapping(uint256 => uint256[]) epochCheckPointBlockNumbers;

    // block number to BlockHeader
    mapping(uint256 => BlockHeader) checkPointBlocks;

    mapping(uint256 => mapping(bytes32 => bool)) epochMmrRoots;

    uint8 relayerThreshold;

    event RelayerThresholdChanged(uint256 newThreshold);
    event RelayerAdded(address relayer);
    event RelayerRemoved(address relayer);

    bytes32 public constant RELAYER_ROLE = keccak256("RELAYER_ROLE");

    // number of the oldest epoch that stored, we use this to prune the expired epochs from state
    uint256 public oldestEpochStored;
    uint256 private constant BLOCK_EXPIRED = 30 days;
    uint256 private constant MAX_PRUNE_ONCE = 2;


    modifier onlyAdmin() {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "sender doesn't have admin role");
        _;
    }

    modifier onlyRelayers() {
        require(hasRole(RELAYER_ROLE, msg.sender), "sender doesn't have relayer role");
        _;
    }

    function adminPauseLightClient() external onlyAdmin {
        _pause();
    }

    function adminUnpauseLightClient() external onlyAdmin {
        _unpause();
    }

    function renounceAdmin(address newAdmin) external onlyAdmin {
        require(msg.sender != newAdmin, 'cannot renounce self');
        grantRole(DEFAULT_ADMIN_ROLE, newAdmin);
        renounceRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    function adminChangeRelayerThreshold(uint256 newThreshold) external onlyAdmin {
        relayerThreshold = newThreshold.toUint8();
        emit RelayerThresholdChanged(newThreshold);
    }

    function adminAddRelayer(address relayerAddress) external onlyAdmin {
        require(!hasRole(RELAYER_ROLE, relayerAddress), "addr already has relayer role!");
        grantRole(RELAYER_ROLE, relayerAddress);
        emit RelayerAdded(relayerAddress);
    }

    function adminRemoveRelayer(address relayerAddress) external onlyAdmin {
        require(hasRole(RELAYER_ROLE, relayerAddress), "addr doesn't have relayer role!");
        revokeRole(RELAYER_ROLE, relayerAddress);
        emit RelayerRemoved(relayerAddress);
    }

    function initialize(
        bytes memory firstRlpHeader,
        address[] memory initialRelayers,
        uint8 initialRelayerThreshold
    ) external initializer {
        HarmonyParser.BlockHeader memory header = HarmonyParser.toBlockHeader(
            firstRlpHeader
        );

        firstBlock.parentHash = header.parentHash;
        firstBlock.stateRoot = header.stateRoot;
        firstBlock.transactionsRoot = header.transactionsRoot;
        firstBlock.receiptsRoot = header.receiptsRoot;
        firstBlock.number = header.number;
        firstBlock.epoch = header.epoch;
        firstBlock.shard = header.shardID;
        firstBlock.time = header.timestamp;
        firstBlock.mmrRoot = HarmonyParser.toBytes32(header.mmrRoot);
        firstBlock.hash = header.hash;
        oldestEpochStored = header.epoch;

        epochCheckPointBlockNumbers[header.epoch].push(header.number);
        checkPointBlocks[header.number] = firstBlock;

        epochMmrRoots[header.epoch][firstBlock.mmrRoot] = true;

        relayerThreshold = initialRelayerThreshold;
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        for (uint256 i; i < initialRelayers.length; i++) {
            grantRole(RELAYER_ROLE, initialRelayers[i]);
        }

    }

    function _deleteEpoch(uint256 epochNo) private {
        uint256[] storage blockNumbers = epochCheckPointBlockNumbers[epochNo];
        for(uint256 i = 0; i < blockNumbers.length; i++) {
            uint256 blockNo = blockNumbers[i];
            bytes32 mmrRoot = checkPointBlocks[blockNo].mmrRoot;
            delete checkPointBlocks[blockNo];
            delete epochMmrRoots[epochNo][mmrRoot];
        }
        delete epochCheckPointBlockNumbers[epochNo];
    }

    function _pruneEpochs(uint256 pruneTime) private {
        uint256 epochCur = oldestEpochStored;
        if (epochCheckPointBlockNumbers[epochCur].length == 0) return;
        for(uint256 i = 0; i < MAX_PRUNE_ONCE; i++) {
            uint256 epochNo = epochCur++;
            uint256[] storage epochBlockNumbers = epochCheckPointBlockNumbers[epochCur];
            uint256 epochBlockCount = epochBlockNumbers.length;
            if (epochBlockCount == 0) break;
            if (checkPointBlocks[epochBlockNumbers[epochBlockCount-1]].time > pruneTime) break;
            _deleteEpoch(epochNo);
        }
        if(epochCur > oldestEpochStored) oldestEpochStored = epochCur;
    }

    function submitCheckpoint(bytes memory rlpHeader) external onlyRelayers whenNotPaused {
        _pruneEpochs(block.timestamp - BLOCK_EXPIRED);
        HarmonyParser.BlockHeader memory header = HarmonyParser.toBlockHeader(
            rlpHeader
        );

        BlockHeader memory checkPointBlock;

        checkPointBlock.parentHash = header.parentHash;
        checkPointBlock.stateRoot = header.stateRoot;
        checkPointBlock.transactionsRoot = header.transactionsRoot;
        checkPointBlock.receiptsRoot = header.receiptsRoot;
        checkPointBlock.number = header.number;
        checkPointBlock.epoch = header.epoch;
        checkPointBlock.shard = header.shardID;
        checkPointBlock.time = header.timestamp;
        checkPointBlock.mmrRoot = HarmonyParser.toBytes32(header.mmrRoot);
        checkPointBlock.hash = header.hash;

        epochCheckPointBlockNumbers[header.epoch].push(header.number);
        checkPointBlocks[header.number] = checkPointBlock;

        epochMmrRoots[header.epoch][checkPointBlock.mmrRoot] = true;
        emit CheckPoint(
            checkPointBlock.stateRoot,
            checkPointBlock.transactionsRoot,
            checkPointBlock.receiptsRoot,
            checkPointBlock.number,
            checkPointBlock.epoch,
            checkPointBlock.shard,
            checkPointBlock.time,
            checkPointBlock.mmrRoot,
            checkPointBlock.hash
        );
    }

    function getLatestCheckPoint(uint256 blockNumber, uint256 epoch)
        public
        view
        returns (BlockHeader memory checkPointBlock)
    {
        require(
            epochCheckPointBlockNumbers[epoch].length > 0,
            "no checkpoints for epoch"
        );
        uint256[] memory checkPointBlockNumbers = epochCheckPointBlockNumbers[epoch];
        uint256 nearest = 0;
        for (uint256 i = 0; i < checkPointBlockNumbers.length; i++) {
            uint256 checkPointBlockNumber = checkPointBlockNumbers[i];
            if (
                checkPointBlockNumber > blockNumber &&
                checkPointBlockNumber < nearest
            ) {
                nearest = checkPointBlockNumber;
            }
        }
        checkPointBlock = checkPointBlocks[nearest];
    }

    function isValidCheckPoint(uint256 epoch, bytes32 mmrRoot) public view returns (bool status) {
        return epochMmrRoots[epoch][mmrRoot];
    }
}
","keccak256":"0x80d4bd94ad856871d0e2b907cc1afc6930210bb0ec274cad03956b1546142034","license":"UNLICENSED"},"contracts/HarmonyParser.sol":{"content":"// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;
pragma experimental ABIEncoderV2;

import "./lib/RLPReader.sol";
import "./lib/RLPEncode.sol";

library HarmonyParser {
    using RLPReader for bytes;
    using RLPReader for uint256;
    using RLPReader for RLPReader.RLPItem;
    using RLPReader for RLPReader.Iterator;

    using RLPEncode for bytes;
    using RLPEncode for bytes[];

    struct BlockHeader {
        bytes32 hash;
        bytes32 parentHash;
        address miner;
        bytes32 stateRoot;
        bytes32 transactionsRoot;
        bytes32 receiptsRoot;
        bytes32 outgoingReceiptsRoot;
        bytes32 incomingReceiptsRoot;
        bytes logsBloom;
        uint256 number;
        uint256 gasLimit;
        uint256 gasUsed;
        uint256 timestamp;
        bytes extraData;
        bytes32 mixHash;
        uint256 viewID;
        uint256 epoch;
        uint256 shardID;
        bytes lastCommitSignature;
        bytes lastCommitBitmap;
        bytes vrf;
        bytes vdf;
        bytes shardState;
        bytes crossLink;
        bytes slashes;
        bytes mmrRoot;
    }

    struct Account {
        uint256 nonce;
        uint256 balance;
        bytes32 storageRoot;
        bytes32 codeHash;
    }

    struct Transaction {
        uint256 nonce;
        uint256 gasPrice;
        uint256 gas;
        uint256 shardID;
        uint256 toShardID;
        address to;
        uint256 value;
        bytes input;
        uint8 v;
        bytes32 r;
        bytes32 s;
    }

    struct Log {
        address contractAddress;
        bytes32[] topics;
        bytes data;
    }

    struct TransactionReceipt {
        bytes32 transactionHash;
        uint256 transactionIndex;
        bytes32 blockHash;
        uint256 blockNumber;
        address from;
        address to;
        uint256 gasUsed;
        uint256 cummulativeGasUsed;
        address contractAddress;
        Log[] logs;
        uint256 status; // root?
        bytes logsBloom;
        uint8 v;
        bytes32 r;
        bytes32 s;
    }

    struct TransactionReceiptTrie {
        uint8 status;
        uint256 gasUsed;
        bytes logsBloom;
        Log[] logs;
    }

    function getBlockRlpData(BlockHeader memory header)
        internal
        pure
        returns (bytes memory data)
    {
        bytes[] memory list = new bytes[](15);

        list[0] = RLPEncode.encodeBytes(abi.encodePacked(header.parentHash));
        list[1] = RLPEncode.encodeAddress(header.miner);
        list[2] = RLPEncode.encodeBytes(abi.encodePacked(header.stateRoot));
        list[3] = RLPEncode.encodeBytes(
            abi.encodePacked(header.transactionsRoot)
        );
        list[4] = RLPEncode.encodeBytes(abi.encodePacked(header.receiptsRoot));
        list[5] = RLPEncode.encodeBytes(
            abi.encodePacked(header.outgoingReceiptsRoot)
        );
        list[6] = RLPEncode.encodeBytes(
            abi.encodePacked(header.incomingReceiptsRoot)
        );
        list[7] = RLPEncode.encodeBytes(header.logsBloom);
        list[8] = RLPEncode.encodeUint(header.number);
        list[9] = RLPEncode.encodeUint(header.gasLimit);
        list[10] = RLPEncode.encodeUint(header.gasUsed);
        list[11] = RLPEncode.encodeUint(header.timestamp);
        list[12] = RLPEncode.encodeBytes(header.extraData);
        list[13] = RLPEncode.encodeBytes(abi.encodePacked(header.mixHash));

        list[14] = RLPEncode.encodeUint(header.viewID);
        list[15] = RLPEncode.encodeUint(header.epoch);
        list[16] = RLPEncode.encodeUint(header.shardID);

        list[17] = RLPEncode.encodeBytes(header.lastCommitSignature);
        list[18] = RLPEncode.encodeBytes(header.lastCommitBitmap);
        list[19] = RLPEncode.encodeBytes(header.vrf);
        list[20] = RLPEncode.encodeBytes(header.vdf);

        list[21] = RLPEncode.encodeBytes(header.shardState);
        list[22] = RLPEncode.encodeBytes(header.crossLink);
        list[23] = RLPEncode.encodeBytes(header.slashes);
        list[24] = RLPEncode.encodeBytes(header.mmrRoot);

        data = RLPEncode.encodeList(list);
    }

    /**
     * Parse RLP-encoded block header into BlockHeader data structure
     *  @param rlpHeader: RLP-encoded block header with data fields order as defined in the BlockHeader struct
     **/
    function toBlockHeader(bytes memory rlpHeader)
        internal
        pure
        returns (BlockHeader memory header)
    {
        RLPReader.Iterator memory it = rlpHeader.toRlpItem().iterator();
        // skip two dummy header fields
        it.next();
        it.next();
        it = it.next().iterator();
        uint256 idx;
        while (it.hasNext()) {
            if (idx == 0) header.parentHash = bytes32(it.next().toUint());
            else if (idx == 1) header.miner = it.next().toAddress();
            else if (idx == 2) header.stateRoot = bytes32(it.next().toUint());
            else if (idx == 3)
                header.transactionsRoot = bytes32(it.next().toUint());
            else if (idx == 4)
                header.receiptsRoot = bytes32(it.next().toUint());
            else if (idx == 5)
                header.outgoingReceiptsRoot = bytes32(it.next().toUint());
            else if (idx == 6)
                header.incomingReceiptsRoot = bytes32(it.next().toUint());
            else if (idx == 7) header.logsBloom = it.next().toBytes();
            else if (idx == 8) header.number = it.next().toUint();
            else if (idx == 9) header.gasLimit = it.next().toUint();
            else if (idx == 10) header.gasUsed = it.next().toUint();
            else if (idx == 11) header.timestamp = it.next().toUint();
            else if (idx == 12) header.extraData = it.next().toBytes();
            else if (idx == 13) header.mixHash = bytes32(it.next().toUint());
            else if (idx == 14) header.viewID = it.next().toUint();
            else if (idx == 15) header.epoch = it.next().toUint();
            else if (idx == 16) header.shardID = it.next().toUint();
            else if (idx == 17)
                header.lastCommitSignature = it.next().toBytes();
            else if (idx == 18) header.lastCommitBitmap = it.next().toBytes();
            else if (idx == 19) header.vrf = it.next().toBytes();
            else if (idx == 20) header.vdf = it.next().toBytes();
            else if (idx == 21) header.shardState = it.next().toBytes();
            else if (idx == 22) header.crossLink = it.next().toBytes();
            else if (idx == 23) header.slashes = it.next().toBytes();
            else if (idx == 24) header.mmrRoot = it.next().toBytes();
            else it.next();

            idx++;
        }
        header.hash = keccak256(rlpHeader);
    }

    function getFirstKey(bytes memory keys) public returns (bytes memory) {
        RLPReader.Iterator memory it = keys.toRlpItem().iterator();
        while (it.hasNext()) {
            bytes memory key = it.next().toBytes();
            return key;
        }
    }

    function calcBlockHeaderHash(bytes memory rlpHeader)
        internal
        pure
        returns (uint256)
    {
        return uint256(keccak256(rlpHeader));
    }

    function calcBlockSealHash(bytes memory rlpHeader)
        internal
        pure
        returns (uint256)
    {
        bytes[] memory rlpFields = new bytes[](13);
        RLPReader.Iterator memory it = rlpHeader.toRlpItem().iterator();
        uint256 idx = 0;
        while (it.hasNext() && idx < 13) {
            rlpFields[idx] = it.next().toRlpBytes();
            idx++;
        }

        bytes memory toSealRlpData = rlpFields.encodeList();
        return uint256(keccak256(toSealRlpData));
    }

    function getBlockHash(BlockHeader memory header)
        internal
        pure
        returns (bytes32 hash)
    {
        return keccak256(getBlockRlpData(header));
    }

    function getLog(Log memory log) internal pure returns (bytes memory data) {
        bytes[] memory list = new bytes[](3);
        bytes[] memory topics = new bytes[](log.topics.length);

        for (uint256 i = 0; i < log.topics.length; i++) {
            topics[i] = RLPEncode.encodeBytes(abi.encodePacked(log.topics[i]));
        }

        list[0] = RLPEncode.encodeAddress(log.contractAddress);
        list[1] = RLPEncode.encodeList(topics);
        list[2] = RLPEncode.encodeBytes(log.data);
        data = RLPEncode.encodeList(list);
    }

    function getReceiptRlpData(TransactionReceiptTrie memory receipt)
        internal
        pure
        returns (bytes memory data)
    {
        bytes[] memory list = new bytes[](4);

        bytes[] memory logs = new bytes[](receipt.logs.length);
        for (uint256 i = 0; i < receipt.logs.length; i++) {
            logs[i] = getLog(receipt.logs[i]);
        }

        list[0] = RLPEncode.encodeUint(receipt.status);
        list[1] = RLPEncode.encodeUint(receipt.gasUsed);
        list[2] = RLPEncode.encodeBytes(receipt.logsBloom);
        list[3] = RLPEncode.encodeList(logs);
        data = RLPEncode.encodeList(list);
    }

    function toReceiptLog(bytes memory data)
        internal
        pure
        returns (Log memory log)
    {
        RLPReader.Iterator memory it = RLPReader.toRlpItem(data).iterator();

        uint256 idx;
        while (it.hasNext()) {
            if (idx == 0) {
                log.contractAddress = it.next().toAddress();
            } else if (idx == 1) {
                RLPReader.RLPItem[] memory list = it.next().toList();
                log.topics = new bytes32[](list.length);
                for (uint256 i = 0; i < list.length; i++) {
                    bytes32 topic = bytes32(list[i].toUint());
                    log.topics[i] = topic;
                }
            } else if (idx == 2) log.data = it.next().toBytes();
            else it.next();
            idx++;
        }
    }

    function toReceipt(bytes memory data)
        internal
        pure
        returns (TransactionReceiptTrie memory receipt)
    {
        RLPReader.Iterator memory it = RLPReader.toRlpItem(data).iterator();

        uint256 idx;
        while (it.hasNext()) {
            if (idx == 0) receipt.status = uint8(it.next().toUint());
            else if (idx == 1) receipt.gasUsed = it.next().toUint();
            else if (idx == 2) receipt.logsBloom = it.next().toBytes();
            else if (idx == 3) {
                RLPReader.RLPItem[] memory list = it.next().toList();
                receipt.logs = new Log[](list.length);
                for (uint256 i = 0; i < list.length; i++) {
                    receipt.logs[i] = toReceiptLog(list[i].toRlpBytes());
                }
            } else it.next();
            idx++;
        }
    }

    function getTransactionRaw(Transaction memory transaction, uint256 chainId)
        internal
        pure
        returns (bytes memory data)
    {
        bytes[] memory list = new bytes[](9);

        list[0] = RLPEncode.encodeUint(transaction.nonce);
        list[1] = RLPEncode.encodeUint(transaction.gasPrice);
        list[2] = RLPEncode.encodeUint(transaction.gas);
        list[3] = RLPEncode.encodeUint(transaction.shardID);
        list[4] = RLPEncode.encodeUint(transaction.toShardID);
        list[5] = RLPEncode.encodeAddress(transaction.to);
        list[6] = RLPEncode.encodeUint(transaction.value);
        list[7] = RLPEncode.encodeBytes(transaction.input);
        list[8] = RLPEncode.encodeUint(chainId);
        list[9] = RLPEncode.encodeUint(0);
        list[10] = RLPEncode.encodeUint(0);
        data = RLPEncode.encodeList(list);
    }

    /**
     * @param rlpTx: RLP-encoded tx with data fields order as defined in the Tx struct
     **/
    function toTransaction(bytes memory rlpTx)
        internal
        pure
        returns (Transaction memory transaction)
    {
        RLPReader.Iterator memory it = rlpTx.toRlpItem().iterator();
        uint256 idx;
        while (it.hasNext()) {
            if (idx == 0) transaction.nonce = it.next().toUint();
            else if (idx == 1) transaction.gasPrice = it.next().toUint();
            else if (idx == 2) transaction.gas = it.next().toUint();
            else if (idx == 3) transaction.shardID = it.next().toUint();
            else if (idx == 4) transaction.toShardID = it.next().toUint();
            else if (idx == 5) transaction.to = it.next().toAddress();
            else if (idx == 6) transaction.value = it.next().toUint();
            else if (idx == 7) transaction.input = it.next().toBytes();
            else if (idx == 8) transaction.v = uint8(it.next().toUint());
            else if (idx == 9) transaction.r = it.next().toBytes32();
            else if (idx == 10) transaction.s = it.next().toBytes32();
            else it.next();
            idx++;
        }
        return transaction;
    }

    function toAccount(bytes memory data)
        internal
        pure
        returns (Account memory account)
    {
        RLPReader.Iterator memory it = RLPReader.toRlpItem(data).iterator();

        uint256 idx;
        while (it.hasNext()) {
            if (idx == 0) account.nonce = it.next().toUint();
            else if (idx == 1) account.balance = it.next().toUint();
            else if (idx == 2)
                account.storageRoot = toBytes32(it.next().toBytes());
            else if (idx == 3)
                account.codeHash = toBytes32(it.next().toBytes());
            else it.next();
            idx++;
        }
    }

    function toBytes32(bytes memory data)
        internal
        pure
        returns (bytes32 _data)
    {
        assembly {
            _data := mload(add(data, 32))
        }
    }
}
","keccak256":"0x31c79dd20aae7b23f420b93609ead77763f216b58e0e8bfbefcf3fcecdd1071c","license":"UNLICENSED"},"contracts/HarmonyProver.sol":{"content":"// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;
pragma experimental ABIEncoderV2;

// largely based on
// https://github.com/loredanacirstea/goldengate/blob/master/contracts/contracts/Prover.sol

import "./HarmonyParser.sol";
import "./HarmonyLightClient.sol";
import "./lib/MMRVerifier.sol";
import "./lib/ECVerify.sol";
import "./lib/MPTValidatorV2.sol";

library HarmonyProver {
    using HarmonyProver for MerkleProof;

    struct MerkleProof {
        bytes32 root;
        uint256 paths;
        bytes proof;
    }

    function verifyTrieProof(MerkleProof memory data)
        internal
        pure
        returns (bytes memory)
    {
        return MPTValidatorV2.validateProof(data.root, data.paths, data.proof);
    }

    function verifyHeader(
        HarmonyParser.BlockHeader memory header,
        MMRVerifier.MMRProof memory proof
    ) internal pure returns (bool valid, string memory reason) {
        bytes32 blockHash = HarmonyParser.getBlockHash(header);
        if (blockHash != header.hash)
            return (false, "Header data or hash invalid");

        // Check block hash was registered in light client
        valid = MMRVerifier.inclusionProof(
            proof.root,
            proof.width,
            proof.index,
            blockHash,
            proof.peaks,
            proof.siblings
        );
        if (!valid) return (false, "verifyHeader - invalid proof");

        return (true, "");
    }

    function verifyTransaction(
        HarmonyParser.BlockHeader memory header,
        MerkleProof memory txdata
    ) internal pure returns (bytes memory serializedTx) {
        require(header.transactionsRoot == txdata.root, "verifyTransaction - different trie roots");
        return txdata.verifyTrieProof();
    }

    function verifyReceipt(
        HarmonyParser.BlockHeader memory header,
        MerkleProof memory receiptdata
    ) internal pure returns (bytes memory serializedReceipt) {
        require(header.receiptsRoot == receiptdata.root, "verifyReceipt - different trie roots");
        return receiptdata.verifyTrieProof();
    }

    function verifyAccount(
        HarmonyParser.BlockHeader memory header,
        MerkleProof memory accountdata
    ) internal pure returns (bytes memory serializedAccount) {
        require(header.stateRoot == accountdata.root, "verifyAccount - different trie roots");
        return accountdata.verifyTrieProof();
    }

    function verifyLog(
        MerkleProof memory receiptdata,
        uint256 logIndex
    ) internal pure returns (bytes memory logdata) {
        bytes memory serializedReceipt = receiptdata.verifyTrieProof();
        HarmonyParser.TransactionReceiptTrie memory receipt = HarmonyParser
            .toReceipt(serializedReceipt);
        return HarmonyParser.getLog(receipt.logs[logIndex]);
    }

    function verifyTransactionAndStatus(
        HarmonyParser.BlockHeader memory header,
        MerkleProof memory receiptdata
    ) internal pure returns (bool valid, string memory reason) {}

    function verifyCode(
        HarmonyParser.BlockHeader memory header,
        MerkleProof memory accountdata
    ) internal pure returns (bool valid, string memory reason) {}

    function verifyStorage(
        MerkleProof memory accountProof,
        MerkleProof memory storageProof
    ) internal pure returns (bytes memory data) {
        HarmonyParser.Account memory account = HarmonyParser.toAccount(
            accountProof.verifyTrieProof()
        );
        require(account.storageRoot == storageProof.root, "verifyStorage - different trie roots");
        return storageProof.verifyTrieProof();
    }

    function getTransactionSender(
        bytes memory txdata,
        uint256 chainId
    ) internal pure returns (address sender) {
        HarmonyParser.Transaction memory transaction = HarmonyParser
            .toTransaction(txdata);
        bytes memory txraw = HarmonyParser.getTransactionRaw(
            transaction,
            chainId
        );

        bytes32 message_hash = keccak256(txraw);
        sender = ECVerify.ecverify(
            message_hash,
            transaction.v,
            transaction.r,
            transaction.s
        );
    }

    function getTransactionHash(bytes memory signedTransaction)
        internal
        pure
        returns (bytes32 hash)
    {
        hash = keccak256(signedTransaction);
    }

    function getBlockHash(HarmonyParser.BlockHeader memory header)
        internal
        pure
        returns (bytes32 hash)
    {
        return keccak256(getBlockRlpData(header));
    }

    function getBlockRlpData(HarmonyParser.BlockHeader memory header)
        internal
        pure
        returns (bytes memory data)
    {
        return HarmonyParser.getBlockRlpData(header);
    }

    function toBlockHeader(bytes memory data)
        internal
        pure
        returns (HarmonyParser.BlockHeader memory header)
    {
        return HarmonyParser.toBlockHeader(data);
    }

    function getLog(HarmonyParser.Log memory log)
        internal
        pure
        returns (bytes memory data)
    {
        return HarmonyParser.getLog(log);
    }

    function getReceiptRlpData(
        HarmonyParser.TransactionReceiptTrie memory receipt
    ) internal pure returns (bytes memory data) {
        return HarmonyParser.getReceiptRlpData(receipt);
    }

    function toReceiptLog(bytes memory data)
        internal
        pure
        returns (HarmonyParser.Log memory log)
    {
        return HarmonyParser.toReceiptLog(data);
    }

    function toReceipt(bytes memory data)
        internal
        pure
        returns (HarmonyParser.TransactionReceiptTrie memory receipt)
    {
        return HarmonyParser.toReceipt(data);
    }

    function toTransaction(bytes memory data)
        internal
        pure
        returns (HarmonyParser.Transaction memory transaction)
    {
        return HarmonyParser.toTransaction(data);
    }

    function toAccount(bytes memory data)
        internal
        pure
        returns (HarmonyParser.Account memory account)
    {
        return HarmonyParser.toAccount(data);
    }
}
","keccak256":"0x89ebdd50c3c8e87b652520218dc31037bd7f504c13f3fd4fec007ab2d6603e53","license":"UNLICENSED"},"contracts/TokenLocker.sol":{"content":"// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;
pragma experimental ABIEncoderV2;

import "./lib/RLPReader.sol";
import "./BridgedToken.sol";
import "./TokenRegistry.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/math/SafeMathUpgradeable.sol";
// import "openzeppelin-solidity/contracts/token/ERC20/IERC20.sol";
// import "openzeppelin-solidity/contracts/token/ERC20/ERC20.sol";
// import "openzeppelin-solidity/contracts/token/ERC20/SafeERC20.sol";
// import "openzeppelin-solidity/contracts/access/Ownable.sol";
// import "openzeppelin-solidity/contracts/math/SafeMath.sol";

contract TokenLocker is TokenRegistry {
    using RLPReader for RLPReader.RLPItem;
    using RLPReader for bytes;
    using SafeERC20Upgradeable for IERC20Upgradeable;
    using SafeMathUpgradeable for uint256;

    event Locked(
        address indexed token,
        address indexed sender,
        uint256 amount,
        address recipient
    );

    event Burn(
        address indexed token,
        address indexed sender,
        uint256 amount,
        address recipient
    );

    bytes32 constant lockEventSig =
        keccak256("Locked(address,address,uint256,address)");
    bytes32 constant burnEventSig =
        keccak256("Burn(address,address,uint256,address)");

    address public otherSideBridge;

    function unlock(
        BridgedToken token,
        address recipient,
        uint256 amount
    ) external {
        require(recipient != address(0), "recipient is a zero address");
        require(
            RxMappedInv[address(token)] != address(0),
            "bridge does not exist"
        );
        token.burnFrom(msg.sender, amount);
        emit Burn(address(token), msg.sender, amount, recipient);
    }

    function lock(
        IERC20Upgradeable token,
        address recipient,
        uint256 amount
    ) external {
        require(recipient != address(0), "recipient is a zero address");
        require(
            TxMapped[address(token)] != address(0),
            "bridge does not exist"
        );
        // grabbing the actual amount that is transferred
        uint256 balanceBefore = token.balanceOf(address(this));
        token.safeTransferFrom(msg.sender, address(this), amount);
        uint256 balanceAfter = token.balanceOf(address(this));
        uint256 actualAmount = balanceAfter.sub(balanceBefore);
        emit Locked(address(token), msg.sender, actualAmount, recipient);
    }

    function toReceiptItems(bytes memory rlpdata) private pure returns(RLPReader.RLPItem[] memory receipt) {
        RLPReader.RLPItem memory stacks = rlpdata.toRlpItem();
        if(rlpdata[0] <= 0x7f) { // if rlpdata[0] between [0,0x7f], it means TransactionType of EIP-2718.
            stacks.memPtr += 1;
            stacks.len -= 1;
        }
        return stacks.toList();
    }

    function execute(bytes memory rlpdata) internal returns (uint256 events) {
        RLPReader.RLPItem[] memory receipt = toReceiptItems(rlpdata);
        // TODO: check txs is revert or not
        uint256 postStateOrStatus = receipt[0].toUint();
        require(postStateOrStatus == 1, "revert receipt");
        RLPReader.RLPItem[] memory logs = receipt[3].toList();
        for (uint256 i = 0; i < logs.length; i++) {
            RLPReader.RLPItem[] memory rlpLog = logs[i].toList();
            address Address = rlpLog[0].toAddress();
            if (Address != otherSideBridge) continue;
            RLPReader.RLPItem[] memory Topics = rlpLog[1].toList(); // TODO: if is lock event
            bytes32[] memory topics = new bytes32[](Topics.length);
            for (uint256 j = 0; j < Topics.length; j++) {
                topics[j] = bytes32(Topics[j].toUint());
            }
            bytes memory Data = rlpLog[2].toBytes();
            if (topics[0] == lockEventSig) {
                onLockEvent(topics, Data);
                events++;
                continue;
            }
            if (topics[0] == burnEventSig) {
                onBurnEvent(topics, Data);
                events++;
                continue;
            }
            if (topics[0] == TokenMapReqEventSig) {
                onTokenMapReqEvent(topics, Data);
                events++;
                continue;
            }
            if (topics[0] == TokenMapAckEventSig) {
                onTokenMapAckEvent(topics);
                events++;
                continue;
            }
        }
    }

    //This argument passing is an excellent example of how to get around stack too deep
    function onBurnEvent(bytes32[] memory topics, bytes memory data) private {
        address token = address(uint160(uint256(topics[1])));
        //address sender = address(uint160(uint256(topics[2])));
        (uint256 amount, address recipient) = abi.decode(
            data,
            (uint256, address)
        );
        IERC20Upgradeable lockedToken = TxMappedInv[token];
        lockedToken.safeTransfer(recipient, amount);
    }

    function onLockEvent(bytes32[] memory topics, bytes memory data) private {
        address token = address(uint160(uint256(topics[1])));
        //address sender = address(uint160(uint256(topics[2])));
        (uint256 amount, address recipient) = abi.decode(
            data,
            (uint256, address)
        );
        BridgedToken mintToken = RxMapped[token];
        require(address(mintToken) != address(0));
        mintToken.mint(recipient, amount);
    }
}
","keccak256":"0x1ba0a065fbbfd12c1e6c3759be8fa786a622f09e833cf535ef47baba745c8d70","license":"UNLICENSED"},"contracts/TokenLockerOnEthereum.sol":{"content":"// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;
pragma experimental ABIEncoderV2;

import "./HarmonyLightClient.sol";
import "./lib/MMRVerifier.sol";
import "./HarmonyProver.sol";
import "./TokenLocker.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

contract TokenLockerOnEthereum is TokenLocker, OwnableUpgradeable {
    HarmonyLightClient public lightclient;

    mapping(bytes32 => bool) public spentReceipt;

    function initialize() external initializer {
        __Ownable_init();
    }

    function changeLightClient(HarmonyLightClient newClient)
        external
        onlyOwner
    {
        lightclient = newClient;
    }

    function bind(address otherSide) external onlyOwner {
        otherSideBridge = otherSide;
    }

    function validateAndExecuteProof(
        HarmonyParser.BlockHeader memory header,
        MMRVerifier.MMRProof memory mmrProof,
        HarmonyProver.MerkleProof memory receiptdata
    ) external {
        require(lightclient.isValidCheckPoint(header.epoch, mmrProof.root), "checkpoint validation failed");
        bytes32 blockHash = HarmonyParser.getBlockHash(header);
        bytes32 rootHash = header.receiptsRoot;
        (bool status, string memory message) = HarmonyProver.verifyHeader(
            header,
            mmrProof
        );
        require(rootHash == receiptdata.root, "invalid proof root");
        require(status, "block header could not be verified");
        bytes32 receiptHash = keccak256(
            abi.encodePacked(blockHash, rootHash, receiptdata.paths)
        );
        require(spentReceipt[receiptHash] == false, "double spent!");
        spentReceipt[receiptHash] = true;
        bytes memory receipt = HarmonyProver.verifyReceipt(header, receiptdata);
        uint256 executedEvents = execute(receipt);
        require(executedEvents > 0, "no valid event");
    }
}
","keccak256":"0x2d5186581afb527ed48d6cc9c7c1bbc76c206a7e6902fc2c5c0ad7689656b281","license":"UNLICENSED"},"contracts/TokenRegistry.sol":{"content":"// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;
pragma experimental ABIEncoderV2;

import "./lib/RLPReader.sol";
// import "openzeppelin-solidity/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
// import "openzeppelin-solidity/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "./BridgedToken.sol";

contract TokenRegistry {
    using RLPReader for RLPReader.RLPItem;
    using RLPReader for bytes;
    using SafeERC20Upgradeable for IERC20Upgradeable;

    event TokenMapReq(
        address indexed tokenReq,
        uint8 indexed decimals,
        string name,
        string symbol
    );

    event TokenMapAck(address indexed tokenReq, address indexed tokenAck);

    bytes32 constant TokenMapReqEventSig =
        keccak256("TokenMapReq(address,uint8,string,string)");
    bytes32 constant TokenMapAckEventSig =
        keccak256("TokenMapAck(address,address)");

    IERC20Upgradeable[] public TxTokens; // thisSide locked ERC20 list
    BridgedToken[] public RxTokens; // bridged token list, keys of

    // TX means token issued in this chain, and cross to another chain
    mapping(address => address) public TxMapped; // thisSide locked => otherSide mint
    mapping(address => IERC20Upgradeable) public TxMappedInv; // inverse KV

    // RX means token issued in another chain, and cross to this chain.
    mapping(address => BridgedToken) public RxMapped; // otherSide locked => thisSide mint
    mapping(address => address) public RxMappedInv; // inverse KV

    function totalBridgedTokens() external view returns (uint256, uint256) {
        return (TxTokens.length, RxTokens.length);
    }

    function issueTokenMapReq(ERC20Upgradeable thisSideToken)
        external
    {
        require(
            TxMapped[address(thisSideToken)] == address(0),
            "token is already mapped"
        );
        ERC20Upgradeable tokenDetail = thisSideToken;
        emit TokenMapReq(
            address(thisSideToken),
            tokenDetail.decimals(),
            tokenDetail.name(),
            tokenDetail.symbol()
        );
    }

    function onTokenMapReqEvent(bytes32[] memory topics, bytes memory data)
        internal
    {
        // event TokenMapReq(address indexed tokenReq, uint256 decimals, string name, string symbol);
        address tokenReq = address(uint160(uint256(topics[1])));
        require(
            address(RxMapped[tokenReq]) == address(0),
            "bridge already exist"
        );
        uint8 decimals = uint8(uint256(topics[2]));
        (string memory name, string memory symbol) = abi.decode(
            data,
            (string, string)
        );
        bytes32 salt = bytes32(uint256(uint160(tokenReq)));
        BridgedToken mintAddress = new BridgedToken{salt: salt}();
        mintAddress.initialize(
            name,
            symbol,
            decimals
        );
        RxMappedInv[address(mintAddress)] = tokenReq;
        RxMapped[tokenReq] = mintAddress;
        RxTokens.push(mintAddress);
        emit TokenMapAck(tokenReq, address(mintAddress));
    }

    function onTokenMapAckEvent(bytes32[] memory topics)
        internal
    {
        address tokenReq = address(uint160(uint256(topics[1])));
        address tokenAck = address(uint160(uint256(topics[2])));
        require(
            TxMapped[tokenReq] == address(0),
            "missing mapping to acknowledge"
        );
        TxMapped[tokenReq] = tokenAck;
        TxMappedInv[tokenAck] = IERC20Upgradeable(tokenReq);
        TxTokens.push(IERC20Upgradeable(tokenReq));
    }
}
","keccak256":"0x63f69fa0ff1363a71f4c04dfdfe8487ecf6b120b1c4c7b37971a52fe87b3090f","license":"UNLICENSED"},"contracts/lib/ECVerify.sol":{"content":"// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

library ECVerify {
    function ecverify(
        bytes32 hash,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) internal pure returns (address signature_address) {
        // Version of signature should be 27 or 28, but 0 and 1 are also possible
        if (v < 27) {
            v += 27;
        }

        // EIP 155: 2 * chainId + 35
        if (v > 28) {
            v = v % 2 == 1 ? 27 : 28;
        }

        require(v == 27 || v == 28);

        signature_address = ecrecover(hash, v, r, s);

        // ecrecover returns zero on error
        require(signature_address != address(0x0), "ECVerify revert");

        return signature_address;
    }
}
","keccak256":"0xc02263ac648d3d7f3f9afe13ca180e09816573363017f8bec216b20fa6b1c9db","license":"UNLICENSED"},"contracts/lib/MMRVerifier.sol":{"content":"// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

/**
 * @author Wanseob Lim <email@wanseob.com>
 * @title Merkle Mountain Range solidity library
 *
 * @dev The index of this MMR implementation starts from 1 not 0.
 *      And it uses keccak256 for its hash function instead of blake2b
 */
library MMRVerifier {
    struct MMRProof {
        bytes32 root;
        uint256 width;
        uint256 index;
        bytes32[] peaks;
        bytes32[] siblings;
    }

    /**
     * @dev It returns true when the given params verifies that the given value exists in the tree or reverts the transaction.
     */
    function inclusionProof(
        bytes32 root,
        uint256 width,
        uint256 index,
        bytes32 value32,
        bytes32[] memory peaks,
        bytes32[] memory siblings
    ) internal pure returns (bool) {
        // bytes32 value32;

        // assembly {
        //     value32 := mload(add(value, 32))
        // }
        uint256 size = getSize(width);
        require(size >= index, "Index is out of range");
        // Check the root equals the peak bagging hash
        require(
            root ==
                // keccak256(
                //     abi.encodePacked(
                //         size,
                //         keccak256(abi.encodePacked(size, peaks))
                //     )
                // ),
                keccak256(
                    abi.encodePacked(
                        peaks
                    )
                ),
            "Invalid root hash from the peaks"
        );

        // Find the mountain where the target index belongs to
        uint256 cursor;
        bytes32 targetPeak;
        uint256[] memory peakIndexes = getPeakIndexes(width);
        for (uint256 i = 0; i < peakIndexes.length; i++) {
            if (peakIndexes[i]-1 >= index) {
                targetPeak = peaks[i];
                cursor = peakIndexes[i]-1;
                break;
            }
        }
        require(targetPeak != bytes32(0), "Target is not found");

        // Find the path climbing down
        uint256[] memory path = new uint256[](siblings.length + 1);
        uint256 left;
        uint256 right;
        uint8 height = uint8(siblings.length) + 1;
        while (height > 0) {
            // Record the current cursor and climb down
            path[--height] = cursor;
            if (cursor == index) {
                // On the leaf node. Stop climbing down
                break;
            } else {
                // On the parent node. Go left or right
                (left, right) = getChildren(cursor+1);
                cursor = index > (left-1) ? (right-1) : (left-1);
                continue;
            }
        }

        // Calculate the summit hash climbing up again
        bytes32 node;
        while (height < path.length) {
            // Move cursor
            cursor = path[height];
            if (height == 0) {
                // cursor is on the leaf
                node = value32;//hashLeaf(cursor, keccak256(value));
            } else if (cursor - 1 == path[height - 1]) {
                // cursor is on a parent and a sibling is on the left
                node = hashBranch(siblings[height - 1], node);
            } else {
                // cursor is on a parent and a sibling is on the right
                node = hashBranch(node, siblings[height - 1]);
            }
            // Climb up
            height++;
        }

        // Computed hash value of the summit should equal to the target peak hash
        require(node == targetPeak, "Hashed peak is invalid");
        return true;
    }

    /**
     * @dev
     */
    function getSize(uint256 width) internal pure returns (uint256) {
        return (width << 1) - numOfPeaks(width);
    }

    /**
     * @dev It returns the hash a parent node with hash(M | Left child | Right child)
     *      M is the index of the node
     */
    function hashBranch(bytes32 left, bytes32 right)
        internal
        pure
        returns (bytes32)
    {
        return keccak256(abi.encodePacked(left, right));
    }

    /**
     * @dev it returns the hash of a leaf node with hash(M | DATA )
     *      M is the index of the node
     */
    function hashLeaf(uint256 index, bytes32 dataHash)
        internal
        pure
        returns (bytes32)
    {
        // return keccak256(abi.encodePacked(index, dataHash));
        return keccak256(abi.encodePacked(dataHash));
    }

    /**
     * @dev It returns the height of the highest peak
     */
    function mountainHeight(uint256 size) internal pure returns (uint8) {
        uint8 height = 1;
        while (uint256(1) << height <= size + height) {
            height++;
        }
        return height - 1;
    }

    /**
     * @dev It returns the height of the index
     */
    function heightAt(uint256 index) internal pure returns (uint8 height) {
        uint256 reducedIndex = index;
        uint256 peakIndex;
        // If an index has a left mountain subtract the mountain
        while (reducedIndex > peakIndex) {
            reducedIndex -= (uint256(1) << height) - 1;
            height = mountainHeight(reducedIndex);
            peakIndex = (uint256(1) << height) - 1;
        }
        // Index is on the right slope
        height = height - uint8((peakIndex - reducedIndex));
    }

    /**
     * @dev It returns the children when it is a parent node
     */
    function getChildren(uint256 index)
        internal
        pure
        returns (uint256 left, uint256 right)
    {
        left = index - (uint256(1) << (heightAt(index) - 1));
        right = index - 1;
        require(left != right, "Not a parent");
    }

    /**
     * @dev It returns all peaks of the smallest merkle mountain range tree which includes
     *      the given index(size)
     */
    function getPeakIndexes(uint256 width)
        internal
        pure
        returns (uint256[] memory peakIndexes)
    {
        peakIndexes = new uint256[](numOfPeaks(width));
        uint256 count;
        uint256 size;
        for (uint256 i = 255; i > 0; i--) {
            if (width & (1 << (i - 1)) != 0) {
                // peak exists
                size = size + (1 << i) - 1;
                peakIndexes[count++] = size;
            }
        }
        require(count == peakIndexes.length, "Invalid bit calculation");
    }

    function numOfPeaks(uint256 width) internal pure returns (uint256 num) {
        uint256 bits = width;
        while (bits > 0) {
            if (bits % 2 == 1) num++;
            bits = bits >> 1;
        }
        return num;
    }
}
","keccak256":"0x3f4852019e7b226bd201cb9f735ac961ef0b91a847fe41326c13eba59ccf926c","license":"UNLICENSED"},"contracts/lib/MPTValidatorV2.sol":{"content":"// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;
pragma experimental ABIEncoderV2;

import "./RLPReader.sol";

/// @dev MPTValidatorV2 is improved by LayerZero https://bscscan.com/address/0xCFf08a35A5f27F306e2DA99ff198dB90f13DEF77#code
library MPTValidatorV2 {
    using RLPReader for RLPReader.RLPItem;
    using RLPReader for RLPReader.Iterator;

    /// @dev Validates a Merkle-Patricia-Trie proof.
    ///      If the proof proves the inclusion of value in the trie, the value is returned.
    ///      It only prove the value is included in the tree, the format and sanity of the value should be checked by caller.
    ///      It will revert if the proof is invalid.
    /// @param rootHash is the Keccak-256 hash of the root node of the MPT.
    /// @param paths select indexes array encoding to uint256.
    /// @param proof is decoded to stack of MPT nodes (starting with the root) that
    ///        need to be traversed during verification.
    /// @return value whose inclusion is proved or an empty byte array for
    ///         a proof of exclusion
    function validateProof(
        bytes32 rootHash,
        uint256 paths,
        bytes memory proof
    ) internal pure returns (bytes memory) {
        RLPReader.RLPItem memory item = RLPReader.toRlpItem(proof);
        RLPReader.Iterator memory iterator = item.iterator();
        uint256 numItems = item.numItems();
        for (uint256 i = 0; i < numItems; i++) {
            item = iterator.next();
            uint256 index = uint8(paths);
            paths >>= 8;
            require(
                rootHash == item.toRlpBytesHash(),
                "ProofLib: invalid hashlink"
            );
            item = item.safeGetItemByIndex(index);
            if (i < numItems - 1) rootHash = bytes32(item.toUint());
        }
        require(paths == 0, "invalid path");
        return item.toBytes();
    }
}
","keccak256":"0xefdd65558e08b32a606648486e2d79311870d42059068ab94be3b13c0c9f1a88","license":"UNLICENSED"},"contracts/lib/RLPEncode.sol":{"content":"// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

/**
 * @title RLPEncode
 * @dev A simple RLP encoding library.
 * @author Bakaoh
 */
library RLPEncode {
    /*
     * Internal functions
     */

    /**
     * @dev RLP encodes a byte string.
     * @param self The byte string to encode.
     * @return The RLP encoded string in bytes.
     */
    function encodeBytes(bytes memory self)
        internal
        pure
        returns (bytes memory)
    {
        bytes memory encoded;
        if (self.length == 1 && uint8(self[0]) <= 128) {
            encoded = self;
        } else {
            encoded = concat(encodeLength(self.length, 128), self);
        }
        return encoded;
    }

    /**
     * @dev RLP encodes a list of RLP encoded byte byte strings.
     * @param self The list of RLP encoded byte strings.
     * @return The RLP encoded list of items in bytes.
     */
    function encodeList(bytes[] memory self)
        internal
        pure
        returns (bytes memory)
    {
        bytes memory list = flatten(self);
        return concat(encodeLength(list.length, 192), list);
    }

    /**
     * @dev RLP encodes a string.
     * @param self The string to encode.
     * @return The RLP encoded string in bytes.
     */
    function encodeString(string memory self)
        internal
        pure
        returns (bytes memory)
    {
        return encodeBytes(bytes(self));
    }

    /**
     * @dev RLP encodes an address.
     * @param self The address to encode.
     * @return The RLP encoded address in bytes.
     */
    function encodeAddress(address self) internal pure returns (bytes memory) {
        bytes memory inputBytes;
        assembly {
            let m := mload(0x40)
            mstore(
                add(m, 20),
                xor(0x140000000000000000000000000000000000000000, self)
            )
            mstore(0x40, add(m, 52))
            inputBytes := m
        }
        return encodeBytes(inputBytes);
    }

    /**
     * @dev RLP encodes a uint.
     * @param self The uint to encode.
     * @return The RLP encoded uint in bytes.
     */
    function encodeUint(uint256 self) internal pure returns (bytes memory) {
        return encodeBytes(toBinary(self));
    }

    /**
     * @dev RLP encodes an int.
     * @param self The int to encode.
     * @return The RLP encoded int in bytes.
     */
    function encodeInt(int256 self) internal pure returns (bytes memory) {
        return encodeUint(uint256(self));
    }

    /**
     * @dev RLP encodes a bool.
     * @param self The bool to encode.
     * @return The RLP encoded bool in bytes.
     */
    function encodeBool(bool self) internal pure returns (bytes memory) {
        bytes memory encoded = new bytes(1);
        encoded[0] = (self ? bytes1(0x01) : bytes1(0x80));
        return encoded;
    }

    /*
     * Private functions
     */

    /**
     * @dev Encode the first byte, followed by the `len` in binary form if `length` is more than 55.
     * @param len The length of the string or the payload.
     * @param offset 128 if item is string, 192 if item is list.
     * @return RLP encoded bytes.
     */
    function encodeLength(uint256 len, uint256 offset)
        private
        pure
        returns (bytes memory)
    {
        bytes memory encoded;
        if (len < 56) {
            encoded = new bytes(1);
            encoded[0] = bytes32(len + offset)[31];
        } else {
            uint256 lenLen;
            uint256 i = 1;
            while (len / i != 0) {
                lenLen++;
                i *= 256;
            }

            encoded = new bytes(lenLen + 1);
            encoded[0] = bytes32(lenLen + offset + 55)[31];
            for (i = 1; i <= lenLen; i++) {
                encoded[i] = bytes32((len / (256**(lenLen - i))) % 256)[31];
            }
        }
        return encoded;
    }

    /**
     * @dev Encode integer in big endian binary form with no leading zeroes.
     * @notice TODO: This should be optimized with assembly to save gas costs.
     * @param _x The integer to encode.
     * @return RLP encoded bytes.
     */
    function toBinary(uint256 _x) private pure returns (bytes memory) {
        bytes memory b = new bytes(32);
        assembly {
            mstore(add(b, 32), _x)
        }
        uint256 i = 0;
        for (; i < 32; i++) {
            if (b[i] != 0) {
                break;
            }
        }
        bytes memory res = new bytes(32 - i);
        for (uint256 j = 0; j < res.length; j++) {
            res[j] = b[i++];
        }
        return res;
    }

    /**
     * @dev Copies a piece of memory to another location.
     * @notice From: https://github.com/Arachnid/solidity-stringutils/blob/master/src/strings.sol.
     * @param _dest Destination location.
     * @param _src Source location.
     * @param _len Length of memory to copy.
     */
    function memcpy(
        uint256 _dest,
        uint256 _src,
        uint256 _len
    ) private pure {
        uint256 dest = _dest;
        uint256 src = _src;
        uint256 len = _len;

        for (; len >= 32; len -= 32) {
            assembly {
                mstore(dest, mload(src))
            }
            dest += 32;
            src += 32;
        }

        uint256 mask = 256**(32 - len) - 1;
        assembly {
            let srcpart := and(mload(src), not(mask))
            let destpart := and(mload(dest), mask)
            mstore(dest, or(destpart, srcpart))
        }
    }

    /**
     * @dev Flattens a list of byte strings into one byte string.
     * @notice From: https://github.com/sammayo/solidity-rlp-encoder/blob/master/RLPEncode.sol.
     * @param _list List of byte strings to flatten.
     * @return The flattened byte string.
     */
    function flatten(bytes[] memory _list) private pure returns (bytes memory) {
        if (_list.length == 0) {
            return new bytes(0);
        }

        uint256 len;
        uint256 i = 0;
        for (; i < _list.length; i++) {
            len += _list[i].length;
        }

        bytes memory flattened = new bytes(len);
        uint256 flattenedPtr;
        assembly {
            flattenedPtr := add(flattened, 0x20)
        }

        for (i = 0; i < _list.length; i++) {
            bytes memory item = _list[i];

            uint256 listPtr;
            assembly {
                listPtr := add(item, 0x20)
            }

            memcpy(flattenedPtr, listPtr, item.length);
            flattenedPtr += _list[i].length;
        }

        return flattened;
    }

    /**
     * @dev Concatenates two bytes.
     * @notice From: https://github.com/GNSPS/solidity-bytes-utils/blob/master/contracts/BytesLib.sol.
     * @param _preBytes First byte string.
     * @param _postBytes Second byte string.
     * @return Both byte string combined.
     */
    function concat(bytes memory _preBytes, bytes memory _postBytes)
        private
        pure
        returns (bytes memory)
    {
        bytes memory tempBytes;

        assembly {
            tempBytes := mload(0x40)

            let length := mload(_preBytes)
            mstore(tempBytes, length)

            let mc := add(tempBytes, 0x20)
            let end := add(mc, length)

            for {
                let cc := add(_preBytes, 0x20)
            } lt(mc, end) {
                mc := add(mc, 0x20)
                cc := add(cc, 0x20)
            } {
                mstore(mc, mload(cc))
            }

            length := mload(_postBytes)
            mstore(tempBytes, add(length, mload(tempBytes)))

            mc := end
            end := add(mc, length)

            for {
                let cc := add(_postBytes, 0x20)
            } lt(mc, end) {
                mc := add(mc, 0x20)
                cc := add(cc, 0x20)
            } {
                mstore(mc, mload(cc))
            }

            mstore(
                0x40,
                and(
                    add(add(end, iszero(add(length, mload(_preBytes)))), 31),
                    not(31)
                )
            )
        }

        return tempBytes;
    }
}
